import React, { useEffect, useState, useContext } from 'react';
import ExamenCard from '../components/ExamenCard';
import { getExamenesByUser, deleteExamen } from '../services/examenService'; // Usa getExamenesByUser
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './ExamenList.css';

const ExamenList = () => {
  const [examenes, setExamenes] = useState([]);
  const { userRole, userId } = useContext(AuthContext); // Obtén el rol y userId del contexto

  useEffect(() => {
    const fetchExamenes = async () => {
      if (userId) { // Asegúrate de que userId esté disponible
        const data = await getExamenesByUser(userId); // Llama a getExamenesByUser con userId
        setExamenes(data);
      }
    };
    fetchExamenes();
  }, [userId]);

  const handleDelete = async (id) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar este examen?')) {
      try {
        await deleteExamen(id);
        setExamenes(examenes.filter((examen) => examen.id !== id));
      } catch (error) {
        console.error('Error al eliminar el examen:', error);
      }
    }
  };

  return (
    <div className="examen-list">
      <h2>Lista de Exámenes</h2>
      {examenes.map((examen) => (
        <ExamenCard
          key={examen.id}
          examen={examen}
          handleDelete={userRole !== 'alumno' ? handleDelete : null} // Solo profesor e independiente pueden eliminar
        />
      ))}

      {/* Mostrar botones según el rol */}
      {userRole !== 'alumno' && (
        <div className="crear-examen-container">
          <Link to="/crear-examen" className="crear-examen-button">
            Crear Nuevo Examen
          </Link>
          {userRole === 'profesor' && (
            <Link to="/crear-examen-txt" className="crear-examen-txt-button">
              Crear Examen desde TXT
            </Link>
          )}
        </div>
      )}
    </div>
  );
};

export default ExamenList;
