import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getPreguntaById, updatePregunta } from '../services/preguntaService';
import './PreguntaForm.css';

const EditarPregunta = () => {
  const { examenId, preguntaId } = useParams();
  const navigate = useNavigate();

  const [descripcion, setDescripcion] = useState('');
  const [explicacion, setExplicacion] = useState('');  // Nuevo estado para la explicación
  const [opciones, setOpciones] = useState(['', '']);  // Mínimo dos opciones por defecto
  const [respuestaCorrecta, setRespuestaCorrecta] = useState('');

  useEffect(() => {
    const fetchPregunta = async () => {
      try {
        const pregunta = await getPreguntaById(preguntaId);

        if (pregunta) {
          setDescripcion(pregunta.texto || '');
          setExplicacion(pregunta.explicacion || '');  // Cargar la explicación si existe
          setOpciones(pregunta.opciones.map(opcion => opcion.texto));
          const correcta = pregunta.opciones.find(opcion => opcion.es_correcta);
          setRespuestaCorrecta(correcta ? correcta.texto : '');
        } else {
          console.error('Pregunta no encontrada');
        }
      } catch (error) {
        console.error('Error al cargar la pregunta:', error);
      }
    };

    fetchPregunta();
  }, [preguntaId]);

  const handleChangeOpcion = (index, value) => {
    const newOpciones = [...opciones];
    newOpciones[index] = value;
    setOpciones(newOpciones);
  };

  const addOpcion = () => {
    setOpciones([...opciones, '']);
  };

  const removeOpcion = (index) => {
    if (opciones.length > 2) {
      const newOpciones = opciones.filter((_, i) => i !== index);
      setOpciones(newOpciones);

      if (opciones[index] === respuestaCorrecta) {
        setRespuestaCorrecta('');
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const opcionesData = opciones.map((texto) => ({
      texto,
      es_correcta: texto === respuestaCorrecta,
    }));

    const preguntaData = {
      texto: descripcion,
      explicacion: explicacion,  // Incluye la explicación
      opciones: opcionesData,
      examen: examenId,
    };

    try {
      await updatePregunta(preguntaId, preguntaData);
      navigate(`/examenes/${examenId}/preguntas`);
    } catch (error) {
      console.error('Error al actualizar la pregunta:', error);
    }
  };

  return (
    <div className="pregunta-form-container">
      <h2>Editar Pregunta</h2>
      <form onSubmit={handleSubmit} className="pregunta-form">
        <div className="form-group">
          <label>Descripción de la Pregunta</label>
          <textarea
            value={descripcion}
            onChange={(e) => setDescripcion(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Explicación (opcional)</label>
          <textarea
            value={explicacion}
            onChange={(e) => setExplicacion(e.target.value)}  // Actualizar la explicación
          />
        </div>

        <div className="form-group">
          <label>Opciones de Respuesta</label>
          {opciones.map((opcion, index) => (
            <div key={index} className="opcion">
              <input
                type="text"
                value={opcion}
                onChange={(e) => handleChangeOpcion(index, e.target.value)}
                required
              />
              {opciones.length > 2 && (
                <button
                  type="button"
                  className="remove-opcion-button"
                  onClick={() => removeOpcion(index)}
                >
                  Eliminar
                </button>
              )}
            </div>
          ))}
          <button type="button" onClick={addOpcion} className="add-opcion-button">
            Añadir Opción
          </button>
        </div>

        <div className="form-group">
          <label>Respuesta Correcta</label>
          <select
            value={respuestaCorrecta}
            onChange={(e) => setRespuestaCorrecta(e.target.value)}
            required
          >
            <option value="">Seleccione una opción</option>
            {opciones.map((opcion, index) => (
              <option key={index} value={opcion}>
                {opcion}
              </option>
            ))}
          </select>
        </div>


        <button type="submit" className="submit-button">Guardar Cambios</button>
      </form>
    </div>
  );
};

export default EditarPregunta;
