// src/pages/EditarExamen.js

import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getExamenById, updateExamen } from '../services/examenService';
import './ExamenForm.css';

const EditarExamen = () => {
  const [nombre, setNombre] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [dividirPorTemas, setDividirPorTemas] = useState(false);
  const [randomizarPreguntas, setRandomizarPreguntas] = useState(false);
  const [randomizarRespuestas, setRandomizarRespuestas] = useState(false);
  const [preguntasPorPagina, setPreguntasPorPagina] = useState('todas');
  const [isLoading, setIsLoading] = useState(true);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchExamen = async () => {
      try {
        const examen = await getExamenById(id);

        if (examen) {
          setNombre(examen.nombre || '');
          setDescripcion(examen.descripcion || '');
          setDividirPorTemas(examen.dividir_por_temas || false);
          setRandomizarPreguntas(examen.randomizar_preguntas || false);
          setRandomizarRespuestas(examen.randomizar_respuestas || false);
          setPreguntasPorPagina(examen.preguntas_por_pagina || 'todas');
        } else {
          console.error('Examen no encontrado');
        }

        setIsLoading(false);
      } catch (error) {
        console.error("Error al cargar el examen:", error);
        setIsLoading(false);
      }
    };

    fetchExamen();
  }, [id]);


  const handleSubmit = async (e) => {
    e.preventDefault();
    const examenData = {
      nombre,
      descripcion,
      dividir_por_temas: dividirPorTemas,
      randomizar_preguntas: randomizarPreguntas,
      randomizar_respuestas: randomizarRespuestas,
      preguntas_por_pagina: preguntasPorPagina,
    };
    try {
      await updateExamen(id, examenData);
      navigate('/examenes');  // Redirigir despuÃ©s de guardar los cambios
    } catch (error) {
      console.error('Error al actualizar el examen:', error);
    }
  };

  if (isLoading) {
    return <div>Cargando datos del examen...</div>;
  }

  return (
    <div className="examen-form-container">
      <h2>Editar Examen</h2>
      <form onSubmit={handleSubmit} className="examen-form">
        <div className="form-group">
          <label>Nombre del Examen</label>
          <input
            type="text"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>DescripciÃ³n</label>
          <textarea
            value={descripcion}
            onChange={(e) => setDescripcion(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label>
            Â¿Dividir examen por temas?
            <input
              type="checkbox"
              checked={dividirPorTemas}
              onChange={(e) => setDividirPorTemas(e.target.checked)}
            />
          </label>
        </div>

        <div className="form-group">
          <label>
            Â¿Randomizar el orden de las preguntas?
            <input
              type="checkbox"
              checked={randomizarPreguntas}
              onChange={(e) => setRandomizarPreguntas(e.target.checked)}
            />
          </label>
        </div>

        <div className="form-group">
          <label>
            Â¿Randomizar el orden de las respuestas?
            <input
              type="checkbox"
              checked={randomizarRespuestas}
              onChange={(e) => setRandomizarRespuestas(e.target.checked)}
            />
          </label>
        </div>

        <div className="form-group">
          <label>NÃºmero de preguntas por pÃ¡gina</label>
          <select
            value={preguntasPorPagina}
            onChange={(e) => setPreguntasPorPagina(e.target.value)}
          >
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="5">5</option>
            <option value="10">10</option>
            <option value="20">20</option>
            <option value="40">40</option>
            <option value="todas">100000</option>
          </select>
        </div>

        <button type="submit" className="submit-btn">Guardar Cambios</button>
      </form>
    </div>
  );
};

export default EditarExamen;
