// src/components/UserArea.js
import React, { useContext, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

import './UserArea.css';

const UserArea = () => {
  const { user, logout } = useContext(AuthContext);
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const navigate = useNavigate();

  const toggleDropdown = () => setDropdownOpen(!isDropdownOpen);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <div className="user-area">
      <button className="user-button" onClick={toggleDropdown}>
        {user?.username || 'Usuario'} ▼
      </button>
      {isDropdownOpen && (
        <div className="dropdown-menu">
          <Link to="/user-details" className="dropdown-item">Datos de usuario</Link>
          <Link to="/change-password" className="dropdown-item">Modificar contraseña</Link>
          <button onClick={handleLogout} className="dropdown-item logout-button">Cerrar sesión</button>
        </div>
      )}
    </div>
  );
};

export default UserArea;
