import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './Header.css';

const Header = () => {
  const { isAuthenticated, userRole, logout } = useContext(AuthContext); // Agregamos `userRole`

  const handleLogout = () => {
    logout();
  };

  return (
    <header className="header">
      <nav>
        <ul>
          <li><Link to="/">Inicio</Link></li>
          {isAuthenticated ? (
            <>
              <li><Link to="/examenes">Exámenes</Link></li>

              {userRole === 'profesor' && (
                <>
                  <li><Link to="/crear-examen" className="highlight">Crear Examen</Link></li>
                  <li><Link to="/estadisticas" className="highlight stats">Estadísticas</Link></li>
                </>
              )}

              {userRole === 'alumno' && (
                <>
                  <li><Link to="/examenes" className="highlight">Mis Exámenes</Link></li>
                </>
              )}

              {userRole === 'independiente' && (
                <>
                  <li><Link to="/crear-examen" className="highlight">Crear Examen</Link></li>
                  <li><Link to="/examenes" className="highlight">Mis Exámenes</Link></li>
                </>
              )}

              <li>
                <button onClick={handleLogout} className="logout-button">Cerrar Sesión</button>
              </li>
            </>
          ) : (
            <>
              <li><Link to="/login" className="login-button">Iniciar Sesión</Link></li>
              <li><Link to="/register" className="register-button">Registrarse</Link></li>
            </>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
