// src/pages/RealizarExamen.js

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getPreguntasByExamen, enviarRespuestas } from '../services/examenService';
import './RealizarExamen.css';

const RealizarExamen = () => {
  const { examenId } = useParams();
  const navigate = useNavigate();

  const [preguntas, setPreguntas] = useState([]);
  const [respuestas, setRespuestas] = useState({});
  const [preguntaActual, setPreguntaActual] = useState(0); // Índice de la pregunta actual
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPreguntas = async () => {
      try {
        const response = await getPreguntasByExamen(examenId);
        setPreguntas(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error al obtener las preguntas:', error);
        setLoading(false);
      }
    };

    fetchPreguntas();
  }, [examenId]);

  const handleSelectRespuesta = (preguntaId, opcionId) => {
    setRespuestas({
      ...respuestas,
      [preguntaId]: opcionId,
    });
  };

  const handleNextPregunta = () => {
    if (preguntaActual < preguntas.length - 1) {
      setPreguntaActual(preguntaActual + 1);
    }
  };

  const handlePreviousPregunta = () => {
    if (preguntaActual > 0) {
      setPreguntaActual(preguntaActual - 1);
    }
  };

  const handleSubmitExamen = async () => {
    try {
      await enviarRespuestas(examenId, respuestas);
      navigate(`/examenes/${examenId}/resultado`);
    } catch (error) {
      console.error('Error al enviar las respuestas:', error);
    }
  };

  if (loading) {
    return <div>Cargando preguntas...</div>;
  }

  const pregunta = preguntas[preguntaActual];

  return (
    <div className="realizar-examen-container">
      <h2>Realizando Examen</h2>
      <div className="pregunta-card">
        <h3>{pregunta.texto}</h3>
        <ul className="opciones-list">
          {pregunta.opciones.map((opcion) => (
            <li key={opcion.id}>
              <label>
                <input
                  type="radio"
                  name={`pregunta-${pregunta.id}`}
                  value={opcion.id}
                  checked={respuestas[pregunta.id] === opcion.id}
                  onChange={() => handleSelectRespuesta(pregunta.id, opcion.id)}
                />
                {opcion.texto}
              </label>
            </li>
          ))}
        </ul>
      </div>
      <div className="navegacion-buttons">
        <button onClick={handlePreviousPregunta} disabled={preguntaActual === 0}>
          Anterior
        </button>
        {preguntaActual < preguntas.length - 1 ? (
          <button onClick={handleNextPregunta}>Siguiente</button>
        ) : (
          <button onClick={handleSubmitExamen}>Enviar Examen</button>
        )}
      </div>
    </div>
  );
};

export default RealizarExamen;
