// src/pages/Estadisticas.js
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { getResultadosByExamen } from '../services/examenService';
import './Estadisticas.css';

const Estadisticas = () => {
  const { examenId } = useParams();
  const [resultados, setResultados] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchResultados = async () => {
      try {
        const data = await getResultadosByExamen(examenId);
        // Añadir número de preguntas correctas en cada resultado
        const resultadosConCorrectas = data.map((resultado) => {
          const preguntasCorrectas = resultado.preguntas.reduce((count, pregunta) => {
            const opcionCorrectaSeleccionada = pregunta.opciones.some(
              (opcion) => opcion.correcta && opcion.seleccionada
            );
            return opcionCorrectaSeleccionada ? count + 1 : count;
          }, 0);
          return { ...resultado, preguntasCorrectas };
        });
        setResultados(resultadosConCorrectas);
        setLoading(false);
      } catch (error) {
        setError('Error al cargar los resultados del examen.');
        setLoading(false);
      }
    };
    fetchResultados();
  }, [examenId]);

  if (loading) {
    return <div>Cargando estadísticas...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="estadisticas-container">
      <h2>Estadísticas del Examen</h2>
      <table className="resultados-table">
        <thead>
          <tr>
            <th>Alumno</th>
            <th>Preguntas Correctas</th>
            <th>Tiempo de Realización</th>
            <th>Fecha</th>
            <th>Ver Detalles</th>
          </tr>
        </thead>
        <tbody>
          {resultados.map((resultado) => (
            <tr key={resultado.id}>
              <td>{resultado.alumno.nombre}</td>
              <td>{resultado.preguntasCorrectas} / {resultado.total_preguntas}</td>
              <td>{resultado.tiempo_empleado}</td>
              <td>{new Date(resultado.fecha_realizacion).toLocaleString()}</td>
              <td>
                <Link to={`/resultados/${resultado.id}`} className="btn detalle-btn">
                  Ver Resultado
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Estadisticas;
