// src/context/AuthContext.js
import React, { createContext, useState, useEffect } from 'react';

// Crea el contexto
export const AuthContext = createContext();

// Proveedor del contexto
export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);  // Estado de autenticación
  const [userRole, setUserRole] = useState(null);  // Estado del rol del usuario

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    const storedRole = localStorage.getItem('userRole'); // Obtener el rol guardado en localStorage
    console.log('Token en localStorage:', token);
    console.log('ROL DE USUARIO: ', storedRole);
    if (token) {
      setIsAuthenticated(true);
      setUserRole(storedRole);  // Establece el rol almacenado si existe
    }
  }, []);

  const login = (token, role) => {
    localStorage.setItem('token', token);
    localStorage.setItem('role', role); // Almacena el rol en localStorage
    console.log('Token y rol guardados en localStorage. Usuario autenticado como:', role);
    setIsAuthenticated(true);
    setUserRole(role);  // Establece el rol actual
  };

  const logout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userRole');  // Elimina el rol de localStorage
    setIsAuthenticated(false);
    setUserRole(null);  // Restablece el rol a null
    console.log('Usuario desconectado y token y rol eliminados de localStorage');
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated, userRole, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
