// src/components/ExamenCard.js
import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import EditarExamen from '../pages/EditarExamen'; // Importa el componente EditarExamen
import './ExamenCard.css';

const ExamenCard = ({ examen, handleDelete }) => {
  const { userRole } = useContext(AuthContext);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  const toggleEditModal = () => {
    setIsEditModalOpen(!isEditModalOpen);
  };

  return (
    <div className="examen-card">
      <h3>{examen.nombre}</h3>
      <p>{examen.descripcion}</p>
      <div className="actions">
        {userRole !== 'profesor' && (
          <Link to={`/examenes/${examen.id}/realizar`} className="btn">Realizar Examen</Link>
        )}
        <button onClick={toggleEditModal} className="btn">Editar Examen</button>
        <Link to={`/examenes/${examen.id}/preguntas`} className="btn">Editar Preguntas</Link>

        {userRole === 'profesor' && (
          <Link to={`/examenes/${examen.id}/estadisticas`} className="btn">Estadísticas</Link>
        )}
        <button onClick={() => handleDelete(examen.id)} className="btn delete-button">
          Eliminar Examen
        </button>
      </div>

      {/* Modal de edición de examen */}
      {isEditModalOpen && (
        <div className="modal-overlay2">
          <div className="modal-content2">
            <button className="close-modal-button2" onClick={toggleEditModal}>×</button>
            <EditarExamen examenId={examen.id} closeModal={toggleEditModal} />
          </div>
        </div>
      )}
    </div>
  );
};

export default ExamenCard;
