// src/services/examenService.js

import axios from 'axios';

const API_URL = 'http://127.0.0.1:8000/gestion/api/';

export const getExamenes = async () => {
  const response = await axios.get(`${API_URL}examenes/`);
  return response.data;
};

// export const getExamenById = async (id) => {
//   const response = await axios.get(`${API_URL}examenes/${id}/`);
//   return response.data;
// };

export const getExamenById = async (id) => {
  try {
    const response = await fetch(`/api/examenes/${id}/`);
    if (!response.ok) {
      throw new Error('No se pudo obtener el examen');
    }
    const examen = await response.json();
    return examen;
  } catch (error) {
    console.error('Error al obtener el examen:', error);
  }
};


export const createExamen = async (examenData) => {
  const response = await axios.post(`${API_URL}examenes/`, examenData);
  return response.data;
};

// export const updateExamen = async (id, examenData) => {
//   const response = await axios.put(`${API_URL}examenes/${id}/`, examenData);
//   return response.data;
// };

export const updateExamen = async (id, examenData) => {
  try {
    const response = await fetch(`/api/examenes/${id}/`, {
      method: 'PUT',  // Puede ser 'PATCH' si estás haciendo actualizaciones parciales
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(examenData),
    });
    if (!response.ok) {
      throw new Error('No se pudo actualizar el examen');
    }
    return await response.json();
  } catch (error) {
    console.error('Error al actualizar el examen:', error);
  }
};


export const getPreguntasExamen = async (id) => {
  const response = await axios.get(`${API_URL}examenes/${id}/preguntas/`);
  return response.data;
};

export const enviarRespuestas = async (id, respuestas) => {
  const response = await axios.post(`${API_URL}examenes/${id}/responder/`, respuestas);
  return response.data;
};

export const getCorreccionExamen = async (id) => {
  const response = await axios.get(`${API_URL}examenes/${id}/correccion/`);
  return response.data;
};
