import React from 'react';
import { Link } from 'react-router-dom';
import './ExamenCard.css';

const ExamenCard = ({ examen, handleDelete, modoIconos }) => {
  return modoIconos ? (
    <div className="examen-icono">
      {/* Mostrar como icono */}
      <Link to={`/examenes/${examen.id}`}>
        <div className="icono-contenido">
          <span role="img" aria-label="examen-icon">ðŸ“„</span> {/* Emoji o Ã­cono */}
          <h3>{examen.nombre}</h3>
        </div>
      </Link>
    </div>
  ) : (
    <div className="examen-card">
      <h3>{examen.nombre}</h3>
      <p>{examen.descripcion}</p>
      <div className="actions">
        <Link to={`/examenes/${examen.id}/realizar`} className="btn">Realizar Examen</Link>
        <Link to={`/examenes/${examen.id}/editar`} className="btn">Editar Examen</Link>
        <button onClick={() => handleDelete(examen.id)} className="btn delete">Eliminar Examen</button>
      </div>
    </div>
  );
};

export default ExamenCard;
