// src/context/AuthContext.js
import React, { createContext, useState, useEffect } from 'react';

// Crea el contexto
export const AuthContext = createContext();

// Proveedor del contexto
export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));
  const [userRole, setUserRole] = useState(localStorage.getItem('role'));
  const [userId, setUserId] = useState(localStorage.getItem('user_id'));
  const [username, setUsername] = useState(localStorage.getItem('username')); // Nuevo estado para el nombre de usuario

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedRole = localStorage.getItem('role');
    const storedUserId = localStorage.getItem('user_id');
    const storedUsername = localStorage.getItem('username'); // Cargar el nombre de usuario del localStorage

    if (token && storedRole && storedUserId && storedUsername) {
      setIsAuthenticated(true);
      setUserRole(storedRole);
      setUserId(storedUserId);
      setUsername(storedUsername); // Establecer el nombre de usuario desde localStorage
    }
  }, []);

  const login = (token, role, user_id, user_name) => {
    localStorage.setItem('token', token);
    localStorage.setItem('role', role);
    localStorage.setItem('user_id', user_id);
    localStorage.setItem('username', user_name); // Guardar el nombre de usuario en localStorage
    setIsAuthenticated(true);
    setUserRole(role);
    setUserId(user_id);
    setUsername(user_name); // Establecer el nombre de usuario
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    localStorage.removeItem('user_id');
    localStorage.removeItem('username'); // Eliminar el nombre de usuario del localStorage
    setIsAuthenticated(false);
    setUserRole(null);
    setUserId(null);
    setUsername(null);
  };

  const updateUsername = (newUsername) => {
    localStorage.setItem('username', newUsername);
    setUsername(newUsername);
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated, userRole, userId, username, login, logout, updateUsername }}>
      {children}
    </AuthContext.Provider>
  );
};
