// src/pages/PreguntaList.js

import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { getPreguntasByExamen, deletePregunta, actualizarOrdenPreguntas } from '../services/preguntaService';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import './PreguntaList.css';

const PreguntaList = () => {
  const { examenId } = useParams();
  const [preguntas, setPreguntas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [colapsadas, setColapsadas] = useState({});
  const [ordenModificado, setOrdenModificado] = useState(false); // Estado para controlar si el orden ha sido modificado

  useEffect(() => {
    const fetchPreguntas = async () => {
      try {
        const response = await getPreguntasByExamen(examenId);
        setPreguntas(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error al obtener las preguntas:', error);
        setLoading(false);
      }
    };

    fetchPreguntas();
  }, [examenId]);

  const handleDelete = async (preguntaId) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar esta pregunta?')) {
      try {
        await deletePregunta(preguntaId);
        setPreguntas(preguntas.filter((pregunta) => pregunta.id !== preguntaId));
      } catch (error) {
        console.error('Error al eliminar la pregunta:', error);
      }
    }
  };

  // Función para convertir índices a letras (a, b, c, ...)
  const indexToLetter = (index) => {
    return String.fromCharCode(97 + index) + ')';  // 97 es el código ASCII de 'a'
  };

  // Alternar la visibilidad de las respuestas
  const toggleColapsar = (preguntaId) => {
    setColapsadas((prevState) => ({
      ...prevState,
      [preguntaId]: !prevState[preguntaId]
    }));
  };

  // Función para manejar el cambio de orden de las preguntas
  const handleOnDragEnd = (result) => {
    if (!result.destination) return;

    const newPreguntas = Array.from(preguntas);
    const [movedPregunta] = newPreguntas.splice(result.source.index, 1);
    newPreguntas.splice(result.destination.index, 0, movedPregunta);

    // Actualizar el estado local
    setPreguntas(newPreguntas);
    setOrdenModificado(true);  // Marcar que el orden ha sido modificado
  };

  // Función para guardar el nuevo orden en el backend
  const guardarOrden = async () => {
    const ordenActualizado = preguntas.map((pregunta, index) => ({
      id: pregunta.id,
      orden: index,
    }));

    try {
      await actualizarOrdenPreguntas(ordenActualizado);
      alert('El orden ha sido guardado correctamente.');
      setOrdenModificado(false);  // Reiniciar el estado de orden modificado
    } catch (error) {
      console.error('Error al actualizar el orden:', error);
      alert('Hubo un error al guardar el nuevo orden.');
    }
  };

  if (loading) {
    return <div>Cargando preguntas...</div>;
  }

  return (
    <div className="pregunta-list-container">
      <h2>Preguntas del Examen</h2>
      <Link to={`/examenes/${examenId}/crear-pregunta`} className="create-button">
        Crear Nueva Pregunta
      </Link>
      <DragDropContext onDragEnd={handleOnDragEnd}>
        <Droppable droppableId="preguntas">
          {(provided) => (
            <ul {...provided.droppableProps} ref={provided.innerRef}>
              {preguntas.length === 0 ? (
                <p>No hay preguntas en este examen.</p>
              ) : (
                preguntas.map((pregunta, index) => (
                  <Draggable key={pregunta.id} draggableId={String(pregunta.id)} index={index}>
                    {(provided) => (
                      <li
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        className="pregunta-card"
                      >
                        <div className="pregunta-header">
                          <strong>{pregunta.texto}</strong>
                          <div className="actions">
                            <Link to={`/examenes/${examenId}/editar-pregunta/${pregunta.id}`} className="edit-button">
                              Editar
                            </Link>
                            <button onClick={() => handleDelete(pregunta.id)} className="delete-button">
                              Eliminar
                            </button>
                          </div>
                        </div>

                        <button
                          onClick={() => toggleColapsar(pregunta.id)}
                          className="toggle-button"
                        >
                          {colapsadas[pregunta.id] ? 'Ocultar Respuestas' : 'Mostrar Respuestas'}
                        </button>

                        {colapsadas[pregunta.id] && (
                          <ul className="respuestas-list">
                            {pregunta.opciones.map((opcion, index) => (
                              <li key={index}>
                                {indexToLetter(index)} {opcion.texto}
                              </li>
                            ))}
                          </ul>
                        )}
                      </li>
                    )}
                  </Draggable>
                ))
              )}
              {provided.placeholder}
            </ul>
          )}
        </Droppable>
      </DragDropContext>

      {/* Mostrar el botón "Guardar Cambios" solo si el orden ha sido modificado */}
      {ordenModificado && (
        <button onClick={guardarOrden} className="guardar-orden-button">
          Guardar Cambios
        </button>
      )}
    </div>
  );
};

export default PreguntaList;
