// src/App.js

import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Home from './pages/Home';  // Importa la página de inicio
import ExamenList from './pages/ExamenList';  // Importa la página de lista de exámenes
import ExamenForm from './pages/ExamenForm';  // Importa la página de creación/edición de exámenes
import Header from './components/Header';  // Importa el encabezado
import './App.css';  // Estilos generales

const App = () => {
  return (
    <Router>
      <div>
        <Header />  {/* El encabezado aparecerá en todas las páginas */}
        <main>
          <Switch>
            <Route exact path="/" component={Home} />  {/* Página de inicio */}
            <Route path="/examenes" component={ExamenList} />  {/* Página de lista de exámenes */}
            <Route path="/crear-examen" component={ExamenForm} />  {/* Página de creación de examen */}
          </Switch>
        </main>
      </div>
    </Router>
  );
};

export default App;
