// src/pages/EditarPregunta.js

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getPreguntaById, updatePregunta } from '../services/preguntaService';
import './PreguntaForm.css';

const EditarPregunta = () => {
  const { examenId, preguntaId } = useParams();
  const navigate = useNavigate();

  const [descripcion, setDescripcion] = useState('');
  const [opciones, setOpciones] = useState([]);
  const [respuestaCorrecta, setRespuestaCorrecta] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPregunta = async () => {
      try {
        const pregunta = await getPreguntaById(preguntaId);
        setDescripcion(pregunta.texto);  // Asumiendo que el campo es 'texto'
        setOpciones(pregunta.opciones || []);  // Cargamos las opciones y prevenimos un 'undefined'
        setRespuestaCorrecta(pregunta.opciones.find((opcion) => opcion.es_correcta)?.texto || '');
        setLoading(false);
      } catch (error) {
        console.error('Error al cargar la pregunta:', error);
        setLoading(false);
      }
    };
    fetchPregunta();
  }, [preguntaId]);

  const handleChangeOpcion = (index, value) => {
    const newOpciones = [...opciones];
    newOpciones[index].texto = value;
    setOpciones(newOpciones);
  };

  const addOpcion = () => {
    setOpciones([...opciones, { texto: '', es_correcta: false }]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const opcionesData = opciones.map((opcion) => ({
      ...opcion,
      es_correcta: opcion.texto === respuestaCorrecta,  // Actualizamos la respuesta correcta
    }));

    const preguntaData = {
      texto: descripcion,
      opciones: opcionesData,
      examen: examenId,
    };

    try {
      await updatePregunta(preguntaId, preguntaData);
      navigate(`/examenes/${examenId}/preguntas`);
    } catch (error) {
      console.error('Error al actualizar la pregunta:', error);
    }
  };

  if (loading) {
    return <div>Cargando datos de la pregunta...</div>;
  }

  return (
    <div className="pregunta-form-container">
      <h2>Editar Pregunta</h2>
      <form onSubmit={handleSubmit} className="pregunta-form">
        <div className="form-group">
          <label>Descripción de la Pregunta</label>
          <textarea
            value={descripcion}
            onChange={(e) => setDescripcion(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Opciones de Respuesta</label>
          {opciones && opciones.length > 0 ? (
            opciones.map((opcion, index) => (
              <div key={index} className="opcion">
                <input
                  type="text"
                  value={opcion.texto}
                  onChange={(e) => handleChangeOpcion(index, e.target.value)}
                  required
                />
              </div>
            ))
          ) : (
            <p>No hay opciones disponibles</p>
          )}
          <button type="button" onClick={addOpcion} className="add-opcion-button">
            Añadir Opción
          </button>
        </div>

        <div className="form-group">
          <label>Respuesta Correcta</label>
          <select
            value={respuestaCorrecta}
            onChange={(e) => setRespuestaCorrecta(e.target.value)}
            required
          >
            <option value="">Seleccione una opción</option>
            {opciones.map((opcion, index) => (
              <option key={index} value={opcion.texto}>
                {opcion.texto}
              </option>
            ))}
          </select>
        </div>

        <button type="submit" className="submit-button">Guardar Cambios</button>
      </form>
    </div>
  );
};

export default EditarPregunta;
