// src/components/UserArea.js
import React, { useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './UserArea.css';

const UserArea = () => {
  const [menuVisible, setMenuVisible] = useState(false);
  const { user, logout } = useContext(AuthContext); // `user` contiene el nombre de usuario
  const navigate = useNavigate();

  const handleLogout = () => {
    logout(); // Cierra sesión usando el contexto de autenticación
    navigate('/');
  };

  return (
    <div className="user-area">
      <span className="username" onClick={() => setMenuVisible(!menuVisible)}>
        {user.username} ▼
      </span>
      {menuVisible && (
        <div className="user-menu">
          <Link to="/datos-usuario" onClick={() => setMenuVisible(false)}>Datos de usuario</Link>
          <Link to="/modificar-contrasena" onClick={() => setMenuVisible(false)}>Modificar contraseña</Link>
          <button onClick={handleLogout}>Cerrar sesión</button>
        </div>
      )}
    </div>
  );
};

export default UserArea;
